import React, { useState } from 'react'

export const Tictac = () => {
    const [val, setVal] = useState(['','','','','','','','','']) 

    function run (i) {
        setVal(val.map( (item, ind) => {
            return ind == i ? item = "x" : null
        } ) )
        console.log(val);
    }
  return (
    <>
        <button onClick={() => run(0)}>{val[0]}</button>
        <button onClick={() => run(1)}>{val[1]}</button>
        <button onClick={() => run(1)}>{val[1]}</button><br></br>
    </>
  )
}
