import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import Posts from '../component/Posts'

export default function Profile() {

    var { id } = useParams()
    const [user, setuser] = useState({})

    const [userPost, setuserPost] = useState(true)
    const [component, setcomponent] = useState('Posts')

    useEffect(() => {
        axios.get(`https://jsonplaceholder.typicode.com/users/${id}`)
            .then((res) => {
                console.log(res.data);
                setuser(res.data)
            })
    }, [])

    return (
        <div>
            <h1>profile</h1>
            <h1>{id}</h1>

            {userPost}

            <section className='card bg-warning col-12 text-center mb-5' >
                <h1>userid = {user.id}</h1>
                <h1>{user.name}</h1>
                <h1>{user.username}</h1>
            </section>

            <section>
                <div className='row justify-content-center'>
                    <button className='col-2 btn btn-success' onClick={() => setuserPost(true)}>Posts</button>
                    <button className='col-2 btn btn-success' onClick={() => setuserPost(false)}>Albums</button>
                </div>
            </section>

            <section>
                <Posts component={userPost == true ? 'Posts' : "Album"}></Posts>
            </section>

        </div>
    )
}
