import axios from 'axios'
import React, { useEffect, useState } from 'react'

export default function Sidebar(props) {

    const [categories, setcategories] = useState([])

    useEffect(() => {
        axios.get('https://dummyjson.com/products/categories')
            .then((res) => {
                console.log(res.data);
                setcategories(res.data)
            })


    }, [])

    return categories  ? <div className='bg-info pt-3'>
        <h3 onClick={() => { props.changeProducts('') }}><span className='btn col-12'>All Products</span></h3>
        {
            categories.map((ele, ind) => {
                return <h3 key={ind} onClick={() => { props.changeProducts(ele) }}><span className='btn col-12'>{ele}</span></h3>
            })
        }
    </div> : <h1>Loading ...</h1>


}
