import React, { useState } from 'react'

function Puzzle() {
    var [val, setval] = useState(['1', '2', '3', '4', '5', '6', '7', '', '8'])

    function run(x) {
        if (val[x - 1] == '') {
            setval(val => val.map((item, index) => {
                return index == x - 1 ? item = val[x] : item
            }))
            setval(val => val.map((item, index) => {
                return index == x ? item = '' : item
            }))
        }
        else if (val[x - 1] == '') {
            setval(val => val.map((item, index) => {
                return index == x - 1 ? item = val[x] : item
            }))
            setval(val => val.map((item, index) => {
                return index == x ? item = '' : item
            }))
        }
    }

    return (
        <div>

            <button onClick={() => run(0)}>{val[0]}</button>
            <button onClick={() => run(1)}>{val[1]}</button>
            <button onClick={() => run(2)}>{val[2]}</button><br></br>
            <button onClick={() => run(3)}>{val[3]}</button>
            <button onClick={() => run(4)}>{val[4]}</button>
            <button onClick={() => run(5)}>{val[5]}</button><br></br>
            <button onClick={() => run(6)}>{val[6]}</button>
            <button onClick={() => run(7)}>{val[7]}</button>
            <button onClick={() => run(8)}>{val[8]}</button>

        </div>
    )
}

export default Puzzle
