import { useState } from "react"
var counter=0, sign
function Tictac () {

    const [val, setVal] = useState(['','','','','','','','',''])





    function run (i) {
        if(val[i] == ''){
            setVal(val.map((item, index) => {
                return index == i ? item = "X" : item
            }))
        }
    }


    return (
        <>

        <button onClick={() => run(0)}>{val[0]}</button>
        <button onClick={() => run(1)}>{val[1]}</button>
        <button onClick={() => run(2)}>{val[2]}</button>
        <button onClick={() => run(3)}>{val[3]}</button>
        <button onClick={() => run(4)}>{val[4]}</button>
        <button onClick={() => run(5)}>{val[5]}</button>
        <button onClick={() => run(6)}>{val[6]}</button>
        <button onClick={() => run(7)}>{val[7]}</button>
        <button onClick={() => run(8)}>{val[8]}</button>

        {console.log(val)}
        
        </>
    )
}

export default Tictac
